con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Groovy Script - EmployeeManagement Start"

//set the propert at TestSuite - Level By testRunner
testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].setPropertyValue("Name","PSRaina")
testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].setPropertyValue("Id","0011")
testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].setPropertyValue("Dept","IT")
testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].setPropertyValue("Age","27")

//Reach/Hit to Add_Employee_TestCase
def xml_AddEmp=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Add_Employee_TestCase"].testSteps["SOAP Request"].getPropertyValue("Request")

//Move that set properties to TestSteps
def name=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].getPropertyValue("Name")
def id=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].getPropertyValue("Id")
def dept=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].getPropertyValue("Dept")
def age=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].getPropertyValue("Age")

//Modify that xml by XmlHolder Class
def Modxml_AddEmp=new XmlHolder(xml_AddEmp)

//Set values to the nodes by setNodeValue()
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:name",name)
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:id",id)
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:Department",dept)
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:age",age)

//Save the modified XML by getXml()
def updatedxml_AddEmp=Modxml_AddEmp.getXml()

//Replace orignal xml with updated one
testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Add_Employee_TestCase"].testSteps["SOAP Request"].setPropertyValue("Request",updatedxml_AddEmp)

//Run that test case : addteststep.run(testRunner.context)
def addtestStep=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Add_Employee_TestCase"].testSteps["SOAP Request"]
def context= new WsdlTestRunContext(addtestStep)
addtestStep.run(testRunner,context)

//END OF Add_Employee_TestCase

def xml_DeltailEmp=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Detail_Employee_TestCase"].testSteps["SOAP Request"].getPropertyValue("Request")

def Modxml_DetailEmp=new XmlHolder(xml_DeltailEmp)

Modxml_DetailEmp.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)

def updatedxml_DetailEmp=Modxml_DetailEmp.getXml()

testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Detail_Employee_TestCase"].testSteps["SOAP Request"].setPropertyValue("Request",updatedxml_DetailEmp)

def addtestStepDetail=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Detail_Employee_TestCase"].testSteps["SOAP Request"]
def contextDetail= new WsdlTestRunContext(addtestStepDetail)
addtestStepDetail.run(testRunner,contextDetail)

//END OF Detail_Employee_TestCase


def xml_DeleteEmp=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Delete_Employee_TestCase"].testSteps["SOAP Request"].getPropertyValue("Request")

def Modxml_DeleteEmp=new XmlHolder(xml_DeleteEmp)

Modxml_DeleteEmp.setNodeValue("//typ:deleteEmployee/typ:employeeName",name)

def updatedxml_DeleteEmp=Modxml_DeleteEmp.getXml()

testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Delete_Employee_TestCase"].testSteps["SOAP Request"].setPropertyValue("Request",updatedxml_DeleteEmp)

def addtestStepDelete=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Delete_Employee_TestCase"].testSteps["SOAP Request"]
def contextDelete= new WsdlTestRunContext(addtestStepDelete)
addtestStepDelete.run(testRunner,contextDelete)


//Validation
def xml_DetailEmpResponse=testRunner.testCase.testSuite.project.testSuites["EmployeePortal_Suite_Automation"].testCases["Detail_Employee_TestCase"].testSteps["SOAP Request"].getPropertyValue("Response")

def hitResponseXml=new XmlHolder(xml_DetailEmpResponse)

def RespValue=hitResponseXml.getNodeValue("//ns:name")

log.info RespValue
log.info name

assert RespValue==name

// End Of Validation
