con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Groovy Script - EmployeePortal Start"

//set the propert at TestSuite - Level By testRunner
testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].setPropertyValue("Name","PPPSingh")
testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].setPropertyValue("Id","0011")
testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].setPropertyValue("Dept","IT")
testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].setPropertyValue("Age","27")

//Reach/Hit to Add_Employee_TestCase
def xml_AddEmp=testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].testCases["Add"].testSteps["SOAP Request"].getPropertyValue("Request")

//Move that set properties to TestSteps
def name=testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].getPropertyValue("Name")
def id=testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].getPropertyValue("Id")
def dept=testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].getPropertyValue("Dept")
def age=testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].getPropertyValue("Age")

//Modify that xml by	Assert.assertEquals(Status.FINISHED, runner.getStatus());the project XmlHolder Class
def Modxml_AddEmp=new XmlHolder(xml_AddEmp)

//Set values to the nodes by setNodeValue()
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:name",name)
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:id",id)
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:Department",dept)
Modxml_AddEmp.setNodeValue("//typ:addEmployee/typ:age",age)

//Save the modified XML by getXml()
def updatedxml_AddEmp=Modxml_AddEmp.getXml()

//Replace orignal xml with updated one
testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].testCases["Add"].testSteps["SOAP Request"].setPropertyValue("Request",updatedxml_AddEmp)

//Run that test case : addteststep.run(testRunner.context)
def addtestStep=testRunner.testCase.testSuite.project.testSuites["Framework_TestSuite"].testCases["Add"].testSteps["SOAP Request"]
def context= new WsdlTestRunContext(addtestStep)
addtestStep.run(testRunner,context)

//END OF Add_Employee_TestCase

assert name=="PPPSingh"
